import Timeselector from "vue-timeselector";
export default {
  data() {
    return {
      title: "",
      time: new Date(),
      clicked: false,
    };
  },
  methods: {
    onSubmit(e) {
      if (this.title.trim() && this.time) {
        const newTodo = {
          id: Date.now(),
          title: this.title,
          time: this.time,
          completed: false,
        };
        this.$emit("add-todo", newTodo);
        this.title = "";
      }
    },
  },
  components: {
    Timeselector,
  },
};
